mapper.select:
SELECT 

FROM game_front_user a

WHERE a.id = #{id}

SELECT 

FROM game_front_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account LIKE 
			'%'||#{account}||'%'
			'%'+#{account}+'%'
			concat('%',#{account},'%')


		AND a.state = #{state}


		AND a.last_logintime BETWEEN #{beginLastLogintime} AND #{endLastLogintime}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM game_front_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE game_front_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
