mapper.select:
SELECT 

FROM zhq_category a

WHERE a.id = #{id}

SELECT 

FROM zhq_category a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.pid = #{parent.id}


		AND a.path = #{parentIds}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.code = #{code}


ORDER BY a.sort ASC

SELECT a.id, a.pid AS "parent.id", a.path FROM zhq_category a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.path LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE zhq_category SET 
	pid = #{parent.id}, 
	path = #{parentIds}
WHERE id = #{id}

UPDATE zhq_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR path LIKE '%,'||#{id}||',%'
